import React from 'react';
import { useGlobalContext } from '../context';

const StartMeditationButton = () => {
  const { sound, language, duration, voice } = useGlobalContext(); // Get the selected values

  const handleClick = () => {
    alert(`Meditation started with:
      Sound: ${sound}
      Language: ${language}
      Duration: ${duration}
      Voice: ${voice}`);
  };

  return (
    <div className="start-button">
      <button className="button start-btn" onClick={handleClick}>START MEDITATION</button>
    </div>
  );
};

export default StartMeditationButton;
